// Fetch api es una librería que permite hacer peticiones a php mediante endpoints
const apiKey= 'G8vzKAm2yKv0RQeEzEhIc5ySqwQ0lUi0';

const peticion= fetch(`https://api.giphy.com/v1/gifs/random?api_key=${apiKey}`);

peticion.then( resp => {
    resp.json().then(data =>{
        console.log(data)
    })
})
.catch(console.warn);
